/*
 * Freshops footer scripts-- called by shop pages using list view, to collapse tables into a mobile-friedly block.
 * Author: Ben Beekman and Michael Hulse
 */function listKerplop(){"use strict";var $kerplops=jQuery(".description-kerplop, .price-header-kerplop, .alpha-header-kerplop, .price-kerplop, .alpha-kerplop");if(jQuery(window).width()<930&&$kerplopActive===!1){$kerplops.kerplop();$kerplopActive=!0;jQuery("form.product_form, .wpsc-add-to-cart-button-form").on("submit",function(){var action_buttons=jQuery('input[name="wpsc_ajax_action"]',jQuery(this)),action;action_buttons.length>0?action=action_buttons.val():action="add_to_cart";form_values=jQuery(this).serialize()+"&action="+action;jQuery("#fancy_notification").length===0&&jQuery("div.wpsc_loading_animation",this).css("visibility","visible");var success=function(response){if(response){if(response.hasOwnProperty("fancy_notification")&&response.fancy_notification&&jQuery("#fancy_notification_content")){jQuery("#fancy_notification_content").html(response.fancy_notification);jQuery("#loading_animation").css("display","none");jQuery("#fancy_notification_content").css("display","block")}jQuery("div.shopping-cart-wrapper").html(response.widget_output);jQuery("div.wpsc_loading_animation").css("visibility","hidden");jQuery(".cart_message").delay(3e3).slideUp(500);jQuery(document).trigger({type:"wpsc_fancy_notification",response:response})}jQuery("#fancy_notification").length>0&&jQuery("#loading_animation").css("display","none")};jQuery.post(wpsc_ajax.ajaxurl,form_values,success,"json");wpsc_fancy_notification(this);return!1})}}var $kerplopActive=!1;jQuery(document).ready(function(){$kerplopActive=!1;listKerplop()});jQuery(window).resize(function(){listKerplop()});jQuery(document).on("wpsc_fancy_notification",function(event){jQuery("#theme-checkout-total").html(event.response.cart_total)});